import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  Box,
  Grid,
  CircularProgress,
  Avatar,
} from "@mui/material";
import Rating from "@mui/material/Rating";
import { getProductReviews } from "../../redux/actions/productActions";
import theme from "../../theme/theme";

const ProductReviewView = ({ productId }) => {
  const dispatch = useDispatch();
  const { reviews, loading, error } = useSelector(
    (state) => state.productReviews
  );

  useEffect(() => {
    dispatch(getProductReviews(productId));
  }, [dispatch, productId]);

  if (!Array.isArray(reviews)) {
    console.error(
      "Reviews should be an array. Check the API response and initial state."
    );
    return (
      <Container>
        <Typography variant="body1" color="error">
          Reviews data is not in an array format.
        </Typography>
      </Container>
    );
  }

  return (
    <Container>
      {loading ? (
        <CircularProgress />
      ) : error ? (
        <Typography variant="body1" color="error">
          {error}
        </Typography>
      ) : reviews.length === 0 ? (
        <Typography variant="body1" color="textSecondary">
          No reviews found
        </Typography>
      ) : (
        <Grid container spacing={2}>
          {reviews.map((review) => (
            <Grid item xs={12} key={review._id}>
              <Box
                sx={{
                  border: "1px solid #ddd",
                  borderRadius: "8px",
                  padding: "16px",
                  marginBottom: "16px",
                }}
              >
                <Avatar
                  sx={{ backgroundColor: theme.palette.grey.main }}
                  variant="square"
                >
                  {review.userId.username}
                </Avatar>

                <Rating name="read-only" value={review.rating} readOnly />
                <Typography variant="body1">{review.comment}</Typography>
              </Box>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default ProductReviewView;
