import React from "react";
import {
  Box,
  Container,
  Grid,
  TextField,
  Typography,
  Button,
  RadioGroup,
  FormControlLabel,
  Radio,
  FormLabel,
  FormControl,
  Divider,
  Badge,
} from "@mui/material";
import axios from "axios";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import { useCart } from "../../../Context/CartContext";

const AddressSchema = Yup.object().shape({
  firstName: Yup.string().required("First Name is required"),
  lastName: Yup.string().required("Last Name is required"),
  phone: Yup.string().required("Phone is required"),
  email: Yup.string().email("Invalid email").required("Email is required"),
  zipcode: Yup.string().required("Zipcode is required"),
  country: Yup.string().required("Country is required"),
  state: Yup.string().required("State is required"),
  city: Yup.string().required("City is required"),
  address: Yup.string().required("Address is required"),
});

const CheckoutSchema = Yup.object().shape({
  billingAddress: AddressSchema,
  shippingAddress: AddressSchema,
  paymentMethod: Yup.string().required("Payment Method is required"),
});

const Checkout = () => {
  const { cart } = useCart();
  const dispatch = useDispatch();

  const calculateTotalPrice = () => {
    return (
      cart.cart?.items?.reduce(
        (total, item) => total + item.productId.price * item.quantity,
        0
      ) || 0
    );
  };

  const initialValues = {
    billingAddress: {
      firstName: "",
      lastName: "",
      phone: "",
      email: "",
      zipcode: "",
      country: "",
      state: "",
      city: "",
      address: "",
    },
    shippingAddress: {
      firstName: "",
      lastName: "",
      phone: "",
      email: "",
      zipcode: "",
      country: "",
      state: "",
      city: "",
      address: "",
    },
    paymentMethod: "COD",
    items:
      cart.cart?.items.map((item) => ({
        productId: item.productId._id,
        quantity: item.quantity,
        price: item.productId.price,
      })) || [],
    amount: calculateTotalPrice(),
  };

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      };

      const { data } = await axios.post("/api/orders", values, config);
      console.log("Order created successfully:", data);
    } catch (error) {
      console.error("Error creating order:", error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Box sx={{ mt: 2 }}>
      <Container>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6} lg={8}>
            <Box sx={{p: 2 }}>
              <Formik
                initialValues={initialValues}
                validationSchema={CheckoutSchema}
                onSubmit={handleSubmit}
              >
                {({ isSubmitting }) => (
                  <Form>
                    <Grid container spacing={3}>
                      {["billingAddress", "shippingAddress"].map((type) => (
                        <Grid key={type} item xs={12} md={12}>
                          <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                            {type === "billingAddress"
                              ? "Billing Details"
                              : "Shipping Details"}
                          </Typography>
                          <Box
                            sx={{
                              boxShadow: 3,
                              p: 2,
                              border: "1px solid #ccc",
                            }}
                          >
                            <Grid container spacing={2}>
                              {Object.keys(initialValues.billingAddress).map(
                                (field) => (
                                  <Grid key={field} item xs={12} md={6}>
                                    <Field
                                      as={TextField}
                                      fullWidth
                                      label={
                                        field.charAt(0).toUpperCase() +
                                        field.slice(1)
                                      }
                                      name={`${type}.${field}`}
                                      size="small"
                                      required
                                    />
                                  </Grid>
                                )
                              )}
                            </Grid>
                          </Box>
                        </Grid>
                      ))}
                      <Grid item xs={12}>
                        <FormControl>
                          <FormLabel>Payment Method</FormLabel>
                          <RadioGroup name="paymentMethod">
                            <FormControlLabel
                              value="COD"
                              control={<Radio />}
                              label="Cash on Delivery"
                            />
                            <FormControlLabel
                              value="Razorpay"
                              control={<Radio />}
                              label="Razorpay"
                            />
                          </RadioGroup>
                        </FormControl>
                      </Grid>
                      <Grid item xs={12}>
                        <Button
                          type="submit"
                          variant="contained"
                          color="primary"
                          disabled={isSubmitting}
                        >
                          Place Order
                        </Button>
                      </Grid>
                    </Grid>
                  </Form>
                )}
              </Formik>
            </Box>
          </Grid>
          <Grid item xs={12} md={6} lg={4}>
            <Box
              sx={{
                boxShadow: 3,
                p: 2,
                border: "1px solid #ccc",
                borderRadius: 3,
              }}
            >
              <Typography variant="h6">Your Order</Typography>
              <Divider sx={{ my: 2 }} />
              {cart.cart?.items.map((item) => (
                <Box
                  key={item.productId._id}
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    mb: 1,
                  }}
                >
                  <Badge badgeContent={item.quantity} color="primary">
                    <img
                      src={item.productId.ProductImage}
                      alt={item.productId.name}
                      style={{
                        height: "60px",
                        width: "60px",
                        borderRadius: "10px",
                      }}
                    />
                  </Badge>
                  <Typography>
                    {item.productId.name} - ₹{item.productId.price}
                  </Typography>
                </Box>
              ))}
              <Divider sx={{ my: 2 }} />
              <Typography variant="h6">
                Total: ₹{calculateTotalPrice().toFixed(2)}
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Checkout;
