import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableRow,
  Paper,
  Box,
  useMediaQuery,
  Card,
  CardContent,
  TableHead,
} from "@mui/material";
import { GetWishlist } from "../redux/actions/productActions";
import EmptyCart from "../assets/image/emptycart.png";
import { SecondaryButton } from "../Global/Button/MuiButton";
import { useNavigate } from "react-router-dom";

const Wishlist = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const wishlist = useSelector((state) => state.wishlist);
  const { loading, wish, error } = wishlist || {};
  const isMobile = useMediaQuery("(max-width:767px)");

  const user = JSON.parse(localStorage.getItem("user"));
  const userId = user?._id;

  useEffect(() => {
    if (userId) {
      console.log("Fetching wishlist for user ID:", userId);
      dispatch(GetWishlist(userId));
    }
  }, [dispatch, userId]);

  console.log("Wishlist Data:", wish);

  return (
    <Container>
      <Box sx={{ m: 2 }}>
        {loading && <CircularProgress />}
        {error && <Typography color="error">{error}</Typography>}
        <Container>
      <Typography variant="h5" sx={{ mb: 2, fontWeight: "bold" }}>
        Your Wishlist ({wishlist.wishlist.totalItems} items)
      </Typography>

      <TableContainer component={Paper} sx={{ borderRadius: 2, boxShadow: 3 }}>
        <Table>
          <TableHead>
            <TableRow sx={{ backgroundColor: "#f5f5f5" }}>
              <TableCell sx={{ fontWeight: "bold" }}>Image</TableCell>
              <TableCell sx={{ fontWeight: "bold" }}>Product Name</TableCell>
              <TableCell sx={{ fontWeight: "bold" }} align="right">
                Price (₹)
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {wishlist.wishlist.items.map((item) => (
              <TableRow key={item.productId}>
                <TableCell>
                  <img
                    src={item.ProductImage}
                    alt={item.name}
                    style={{ width: 80, height: 80, borderRadius: "10px" }}
                  />
                </TableCell>
                <TableCell>
                  <Typography fontWeight="bold">{item.name}</Typography>
                </TableCell>
                <TableCell align="right">₹{item.price.toFixed(2)}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default WishlistTable;


        {wish?.items?.length > 0 ? (
          <>
            {isMobile ? (
              wish.items.map((item) => (
                <Card
                  key={item.productId?._id}
                  sx={{
                    display: "flex",
                    mb: 2,
                    boxShadow: 2,
                    borderRadius: 2,
                    p: 1,
                  }}
                >
                  <img
                    src={item.productId?.ProductImage}
                    alt={item.productId?.name}
                    style={{
                      height: "80px",
                      width: "80px",
                      borderRadius: "10px",
                      marginTop: "25px",
                    }}
                  />
                  <CardContent sx={{ flex: 1, p: 1 }}>
                    <Box
                      sx={{ display: "flex", justifyContent: "space-between" }}
                    >
                      <Typography variant="body1">
                        {item.productId?.name}
                      </Typography>
                    </Box>
                    <Box
                      sx={{ display: "flex", justifyContent: "space-between" }}
                    >
                      <Typography variant="body2">Price:</Typography>
                      <Typography>₹{item.productId?.price}</Typography>
                    </Box>
                  </CardContent>
                </Card>
              ))
            ) : (
              <TableContainer
                component={Paper}
                sx={{ overflowX: "auto", borderRadius: 2, boxShadow: 3 }}
              >
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell></TableCell>
                      <TableCell>Product</TableCell>
                      <TableCell align="right">Price</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {wish.items.map((item) => (
                      <TableRow key={item.productId?._id}>
                        <TableCell>
                          <img
                            src={item.productId?.ProductImage}
                            alt={item.productId?.name}
                            style={{
                              height: "80px",
                              width: "80px",
                              borderRadius: "10px",
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <Typography fontWeight="bold">
                            {item.productId?.name}
                          </Typography>
                        </TableCell>
                        <TableCell align="right">
                          ₹{item.productId?.price}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            )}
          </>
        ) : (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              m: 2,
            }}
          >
            <img
              src={EmptyCart}
              alt="Empty Cart"
              style={{ width: "100px", height: "100px" }}
            />
            <Typography sx={{ fontSize: 20, fontWeight: 500, my: 1 }}>
              Your wishlist is empty.
            </Typography>
            <SecondaryButton
              text="START SHOPPING"
              onClick={() => navigate("/collection")}
            />
          </Box>
        )}
      </Box>
    </Container>
  );
};

export default Wishlist;
