import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Box,
  Button,
} from "@mui/material";
import { Add, Remove, Delete } from "@mui/icons-material";
import {
  GetAddToCart,
  removeFromCart,
  addToCart,
} from "../redux/actions/productActions";
import EmptyCart from "../assets/image/emptycart.png";

function Cart() {
  const dispatch = useDispatch();
  const cartList = useSelector((state) => state.cartList);
  const { loading, cart, error } = cartList.cart || {};

  const user = JSON.parse(localStorage.getItem("user"));
  const userId = user?._id;

  useEffect(() => {
    if (userId) {
      dispatch(GetAddToCart(userId));
    }
  }, [dispatch, userId]);

  const handleRemove = (productId) => {
    dispatch(removeFromCart(productId, userId));
  };

  const handleIncrease = (productId, quantity) => {
    dispatch(addToCart(userId, productId, quantity + 1));
  };

  const handleDecrease = (productId, quantity) => {
    if (quantity > 1) {
      dispatch(addToCart(userId, productId, quantity - 1));
    } else {
      dispatch(removeFromCart(productId, userId));
    }
  };

  const calculateTotalPrice = () => {
    return (
      cart?.items?.reduce(
        (total, item) => total + item.productId.price * item.quantity,
        0
      ) || 0
    );
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Shopping Cart
      </Typography>
      {loading && <CircularProgress />}
      {error && <Typography color="error">{error}</Typography>}
      {cart?.items?.length > 0 ? (
        <>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell></TableCell>
                  <TableCell>Product</TableCell>
                  <TableCell align="right">Price</TableCell>
                  <TableCell align="right">Quantity</TableCell>
                  <TableCell align="right">Total</TableCell>
                  <TableCell align="center">Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {cart.items.map((item) => (
                  <TableRow key={item.productId._id}>
                    <TableCell component="th" scope="row">
                      <img
                        src={item.productId.ProductImage}
                        alt={item.productId.name}
                        style={{
                          marginRight: "10px",
                          height: "80px",
                          width: "80px",
                          borderRadius: "20px",
                        }}
                      />
                    </TableCell>
                    <TableCell align="left">
                      <Typography sx={{ fontWeight: "bold", fontSize: "18px" }}>
                        {item.productId.name}
                      </Typography>
                    </TableCell>
                    <TableCell align="right">${item.productId.price}</TableCell>
                    <TableCell align="right">
                      <IconButton
                        onClick={() =>
                          handleDecrease(item.productId._id, item.quantity)
                        }
                      >
                        <Remove />
                      </IconButton>
                      {item.quantity}
                      <IconButton
                        onClick={() =>
                          handleIncrease(item.productId._id, item.quantity)
                        }
                      >
                        <Add />
                      </IconButton>
                    </TableCell>
                    <TableCell align="right">
                      ${(item.productId.price * item.quantity).toFixed(2)}
                    </TableCell>
                    <TableCell align="center">
                      <IconButton
                        color="secondary"
                        onClick={() => handleRemove(item.productId._id)}
                      >
                        <Delete />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
          <Box mt={4} p={2} bgcolor="#f5f5f5" borderRadius={2}>
            <Typography variant="h5" align="right">
              Total Price: ${calculateTotalPrice().toFixed(2)}
            </Typography>
          </Box>
        </>
      ) : (
        <Box>
          <img src={EmptyCart} alt="images"/>
          <Typography>Your cart is currently empty</Typography>
        </Box>
      )}
    </Container>
  );
}

export default Cart;
