import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { GetAddToCart, removeFromCart } from "../redux/actions/productActions";
import { Container, Typography, Card, CardContent, CardMedia, Grid, Button, CircularProgress } from "@mui/material";

function Cart() {
  const dispatch = useDispatch();
  const cartList = useSelector((state) => state.cartList);
  const { loading, cart, error } = cartList || {};

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user && user._id) {
      dispatch(GetAddToCart(user._id));
    }
  }, [dispatch]);

  const handleRemove = (cartId, userId) => {
    dispatch(removeFromCart(cartId, userId));
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Shopping Cart</Typography>
      {loading && <CircularProgress />}
      {error && <Typography color="error">{error}</Typography>}
      {cart?.items?.length > 0 ? (
        <Grid container spacing={2}>
          {cart.items.map((item) => (
            <Grid item xs={12} sm={6} md={4} key={item.productId._id}>
              <Card>
                <CardMedia
                  component="img"
                  height="140"
                  image={item.productId.ProductImage}
                  alt={item.productId.name}
                />
                <CardContent>
                  <Typography variant="h6">{item.productId.name}</Typography>
                  <Typography variant="body1">${item.productId.price} x {item.quantity}</Typography>
                  <Button variant="contained" color="secondary" sx={{ mt: 1 }} onClick={() => handleRemove(cart._id, cart.userId)}>Remove</Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      ) : (
        <Typography>No items in cart.</Typography>
      )}
    </Container>
  );
}

export default Cart;
