import React, { useState } from "react";
import {
  Box,
  Container,
  Grid,
  TextField,
  Typography,
  Button,
  RadioGroup,
  FormControlLabel,
  Radio,
  FormLabel,
  FormControl,
  Divider,
  Checkbox,
  Badge,
} from "@mui/material";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import { useCart } from "../../../Context/CartContext";
import { useDispatch } from "react-redux";
import { createOrder } from "../../../redux/actions/productActions";

const AddressSchema = Yup.object().shape({
  firstName: Yup.string().required("First Name is required"),
  lastName: Yup.string().required("Last Name is required"),
  phone: Yup.string().required("Phone is required"),
  email: Yup.string().email("Invalid email").required("Email is required"),
  zipcode: Yup.string().required("Zipcode is required"),
  country: Yup.string().required("Country is required"),
  state: Yup.string().required("State is required"),
  city: Yup.string().required("City is required"),
  address: Yup.string().required("Address is required"),
});

const CheckoutSchema = Yup.object().shape({
  billingAddress: AddressSchema,
  shippingAddress: AddressSchema,
  paymentMethod: Yup.string().required("Payment Method is required"),
});

const Checkout = () => {
  const { cart } = useCart();
  const dispatch = useDispatch();
  const [sameAsBilling, setSameAsBilling] = useState(false);

  const calculateTotalPrice = () => {
    return (
      cart.cart?.items?.reduce(
        (total, item) => total + item.productId.price * item.quantity,
        0
      ) || 0
    );
  };

  const handleSubmit = (values, { setSubmitting }) => {
    dispatch(createOrder(values));
    alert("Order Placed Successfully!");
    setSubmitting(false);
  };

  const initialValues={
    billingAddress: {
      firstName: "",
      lastName: "",
      phone: "",
      email: "",
      zipcode: "",
      country: "",
      state: "",
      city: "",
      address: "",
    },
    shippingAddress: {
      firstName: "",
      lastName: "",
      phone: "",
      email: "",
      zipcode: "",
      country: "",
      state: "",
      city: "",
      address: "",
    },
    paymentMethod: "COD",
  }

  return (
    <Container>
      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          <Formik
            
            validationSchema={CheckoutSchema}
            onSubmit={handleSubmit}
          >
            {({ isSubmitting, values, setFieldValue }) => (
              <Form>
                <Grid container spacing={3}>
                  {/* Billing Address */}
                  <Grid item xs={12}>
                    <Typography variant="h6">Billing Address</Typography>
                    <Grid container spacing={2}>
                      {Object.keys(AddressSchema.fields).map((field) => (
                        <Grid key={field} item xs={12} md={6}>
                          <Field
                            as={TextField}
                            fullWidth
                            label={field}
                            name={`billingAddress.${field}`}
                            size="small"
                          />
                          <ErrorMessage
                            name={`billingAddress.${field}`}
                            component="div"
                            style={{ color: "red" }}
                          />
                        </Grid>
                      ))}
                    </Grid>
                  </Grid>

                  {/* Checkbox for Copying Address */}
                  <Grid item xs={12}>
                    <FormControlLabel
                      control={
                        <Checkbox
                          checked={sameAsBilling}
                          onChange={(e) => {
                            setSameAsBilling(e.target.checked);
                            if (e.target.checked) {
                              setFieldValue(
                                "shippingAddress",
                                values.billingAddress
                              );
                            } else {
                              setFieldValue("shippingAddress", {
                                firstName: "",
                                lastName: "",
                                phone: "",
                                email: "",
                                zipcode: "",
                                country: "",
                                state: "",
                                city: "",
                                address: "",
                              });
                            }
                          }}
                        />
                      }
                      label="Same as Billing Address"
                    />
                  </Grid>

                  {/* Shipping Address */}
                  {!sameAsBilling && (
                    <Grid item xs={12}>
                      <Typography variant="h6">Shipping Address</Typography>
                      <Grid container spacing={2}>
                        {Object.keys(AddressSchema.fields).map((field) => (
                          <Grid key={field} item xs={12} md={6}>
                            <Field
                              as={TextField}
                              fullWidth
                              label={field}
                              name={`shippingAddress.${field}`}
                              size="small"
                            />
                            <ErrorMessage
                              name={`shippingAddress.${field}`}
                              component="div"
                              style={{ color: "red" }}
                            />
                          </Grid>
                        ))}
                      </Grid>
                    </Grid>
                  )}

                  {/* Payment Method */}
                  <Grid item xs={12}>
                    <FormControl>
                      <FormLabel>Payment Method</FormLabel>
                      <RadioGroup name="paymentMethod">
                        <FormControlLabel
                          value="COD"
                          control={<Radio />}
                          label="Cash on Delivery"
                        />
                        <FormControlLabel
                          value="Razorpay"
                          control={<Radio />}
                          label="Razorpay"
                        />
                      </RadioGroup>
                    </FormControl>
                  </Grid>

                  {/* Submit Button */}
                  <Grid item xs={12}>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      disabled={isSubmitting}
                    >
                      {values.paymentMethod === "Razorpay"
                        ? "Pay Now"
                        : "Place Order"}
                    </Button>
                  </Grid>
                </Grid>
              </Form>
            )}
          </Formik>
        </Grid>
        <Grid item xs={12} md={6} lg={4}>
          <Box
            sx={{
              boxShadow: 3,
              p: 2,
              border: "1px solid #ccc",
              borderRadius: 3,
            }}
          >
            <Typography variant="h6">Your Order</Typography>
            <Divider sx={{ my: 2 }} />
            {cart.cart?.items.map((item) => (
              <Box      
                key={item.productId._id}
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  mb: 1,
                }}
              >
                <Badge badgeContent={item.quantity} color="primary">
                  <img
                    src={item.productId.ProductImage}
                    alt={item.productId.name}
                    style={{
                      height: "60px",
                      width: "60px",
                      borderRadius: "10px",
                    }}
                  />
                </Badge>
                <Typography>
                  {item.productId.name} - ₹{item.productId.price}
                </Typography>
              </Box>
            ))}
            <Divider sx={{ my: 2 }} />
            <Typography variant="h6">
              Total: ₹{calculateTotalPrice().toFixed(2)}
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Checkout;
