import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Box,
  Typography,
  CircularProgress,
  Grid,
  Container,
  Breadcrumbs,
  Divider,
  Button,
} from "@mui/material";
import {
  FacebookShareButton,
  TwitterShareButton,
  WhatsappShareButton,
  FacebookIcon,
  TwitterIcon,
  WhatsappIcon,
} from "react-share";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import {
  addToCart,
  addWishList,
  getProductDetails,
  getRelatedProducts,
  removeWishlist,
} from "../redux/actions/productActions";
import theme from "../theme/theme";
import ProductModel from "./ProductModel";
import InquiryModel from "./InquiryModel";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";

function ProductDetails() {
  const { productId } = useParams();
  const dispatch = useDispatch();
  const [quantity, setQuantity] = useState(1);
  const [openModal, setOpenModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [openInquiryModal, setOpenInquiryModal] = useState(false);

  const productDetails = useSelector((state) => state.productDetails);
  const { loading, product, error } = productDetails;
  const { wishlist } = useSelector((state) => state.wishlist);
  const { products: relatedProducts, loading: relatedLoading } = useSelector(
    (state) => state.relatedProducts
  );

  const user = JSON.parse(localStorage.getItem("user"));
  const productUrl = `${window.location.origin}/product/${productId}`;

  useEffect(() => {
    dispatch(getProductDetails(productId));
    dispatch(getRelatedProducts(productId));
  }, [dispatch, productId]);

  useEffect(() => {
    if (wishlist && product) {
      setIsWishlisted(
        wishlist.some((item) => item.productId === product._id && item.liked)
      );
    }
  }, [wishlist, product]);

  const handleOpenModal = (product) => {
    setSelectedProduct(product);
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setSelectedProduct(null);
  };

  const handleInquiryOpen = () => setOpenInquiryModal(true);
  const handleInquiryClose = () => setOpenInquiryModal(false);

  const handleIncrement = () => setQuantity((prev) => prev + 1);
  const handleDecrement = () => setQuantity((prev) => Math.max(prev - 1, 1));

  const handleAddToCart = () => {
    if (!user) {
      alert("Please log in first");
      return;
    }
    dispatch(addToCart(user._id, product._id, quantity));
  };

  const handleWishlistToggle = () => {
    if (!user) {
      alert("Please log in first");
      return;
    }
    isWishlisted
      ? dispatch(removeWishlist(user._id, product._id))
      : dispatch(addWishList(user._id, product._id));
  };

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", height: "100vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return <Typography variant="h6" color="error">{error}</Typography>;
  }

  if (!product) {
    return <Typography variant="h6">Product not found</Typography>;
  }

  return (
    <Container>
      <Breadcrumbs aria-label="breadcrumb" sx={{ mb: 4 }}>
        <Link to="/">Home</Link>
        <Link to={`/category/${product.category?.name}`}>{product.category?.name || "Not available"}</Link>
        <Typography color="text.primary">{product.name}</Typography>
      </Breadcrumbs>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6} sx={{ textAlign: "center" }}>
          <img src={product.ProductImage} alt={product.name} style={{ width: "100%", borderRadius: "8px" }} />
        </Grid>

        <Grid item xs={12} md={6}>
          <Typography variant="h5" fontWeight="bold">{product.name}</Typography>
          <Typography variant="body1">Category: {product.category?.name || "Not available"}</Typography>
          <Typography variant="body1">SKU: {product.sku}</Typography>
          <Typography variant="body1">{product.description}</Typography>
          <Typography variant="body1">
            <b>&#8377; {product.price}</b>
            {product.oldPrice && (
              <span style={{ textDecoration: "line-through", marginLeft: "10px", color: "red" }}>
                &#8377; {product.oldPrice}
              </span>
            )}
          </Typography>

          <Divider sx={{ my: 2 }} />

          <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
            <Button onClick={handleDecrement}>-</Button>
            <Typography>{quantity}</Typography>
            <Button onClick={handleIncrement}>+</Button>
            <Button onClick={handleAddToCart}>Add to Cart</Button>
            <Box onClick={handleWishlistToggle} sx={{ cursor: "pointer" }}>
              {isWishlisted ? <FavoriteIcon sx={{ color: "red" }} /> : <FavoriteBorderIcon />}
            </Box>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
}

export default ProductDetails;
