// src/components/ProductReviewView.js
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  Box,
  Grid,
  CircularProgress,
} from "@mui/material";
import Rating from "@mui/material/Rating";
import { getProductReviews } from "../../redux/actions/productActions";

const ProductReviewView = ({ productId }) => {
  console.log(productId);
  
  const dispatch = useDispatch();
  const { reviews, loading, error } = useSelector((state) => state.productReviews);

  useEffect(() => {
    dispatch(getProductReviews(productId));
  }, [dispatch, productId]);

  return (
    <Container>
      {loading ? (
        <CircularProgress />
      ) : error ? (
        <Typography variant="body1" color="error">
          {error}
        </Typography>
      ) : reviews.length === 0 ? (
        <Typography variant="body1" color="textSecondary">
          No reviews found
        </Typography>
      ) : (
        <Grid container spacing={2}>
          {reviews.map((review) => (
            <Grid item xs={12} key={review._id}>
              <Box
                sx={{
                  border: "1px solid #ddd",
                  borderRadius: "8px",
                  padding: "16px",
                  marginBottom: "16px",
                }}
              >
                <Rating name="read-only" value={review.rating} readOnly />
                <Typography variant="body1">{review.comment}</Typography>
                <Typography variant="subtitle2">By: {review.userId.username}</Typography>
              </Box>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default ProductReviewView;
