import React, { useEffect } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import {
  TextField,
  Button,
  Container,
  Typography,
  Box,
  CircularProgress,
} from "@mui/material";
import "react-toastify/dist/ReactToastify.css";
import { toast, ToastContainer } from "react-toastify";
import { RegisterData } from "../../redux/actions/productActions";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import theme from "../../theme/theme";

const Login = () => {
  const dispatch = useDispatch();
  const { error, data } = useSelector((state) => state.Register);

  // Formik configuration
  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema: Yup.object({
      email: Yup.string()
        .email("Invalid email format")
        .required("Email is required"),
      password: Yup.string()
        .min(6, "Password must be at least 6 characters")
        .required("Password is required"),
    }),
    onSubmit: (values, { resetForm }) => {
      dispatch(RegisterData(values));
      resetForm();
    },
  });

  useEffect(() => {
    if (data) {
      toast.success(data);
      formik.resetForm();
    } else if (error) {
      toast.error(error);
    }
    // eslint-disable-next-line
  }, [error, data]);

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 5, p: 3, boxShadow: 3, borderRadius: 2 }}>
        <ToastContainer positionposition="top-center" />
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          <img
            src={logo}
            alt="logo"
            style={{
              width: "100%",
              maxWidth: "100px",
              height: "100px",
              margin: "0 auto",
              textAlign: "center",
            }}
          />
        </Box>
        <Typography variant="h5" align="center" gutterBottom>
          Login
        </Typography>
        <form onSubmit={formik.handleSubmit}>
          {/* Email Field */}
          <TextField
            fullWidth
            label="Email"
            name="email"
            variant="outlined"
            margin="normal"
            size="small"
            value={formik.values.email}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            error={formik.touched.email && Boolean(formik.errors.email)}
            helperText={formik.touched.email && formik.errors.email}
          />

          {/* Password Field */}
          <TextField
            fullWidth
            label="Password"
            name="password"
            type="password"
            variant="outlined"
            size="small"
            margin="normal"
            value={formik.values.password}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            error={formik.touched.password && Boolean(formik.errors.password)}
            helperText={formik.touched.password && formik.errors.password}
          />

          {/* Submit Button */}
          <Button
            type="submit"
            variant="outlined"
            color="primary"
            sx={{
              mt: 2,
              maxWidth: "120px",
              width: "100%",
              margin: "10px auto",
              display: "block",
              borderRadius: "50px",
            }}
            disabled={formik.isSubmitting}
          >
            {formik.isSubmitting ? <CircularProgress size={24} /> : "Register"}
          </Button>

          <Typography
            sx={{
              textAlign: "center",
            }}
          >
            Don't have account?
            <Link
              to="/login"
              style={{
                color: theme.palette.primary.main,
                margin: "0 10px",
              }}
            >
              Login
            </Link>
          </Typography>
        </form>
      </Box>
    </Container>
  );
};

export default Login;
