import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableRow,
  Paper,
  Box,
  useMediaQuery,
  Card,
  CardContent,
  TableHead,
} from "@mui/material";
import {
  addToCart,
  GetWishlist,
  removeWishlist,
} from "../redux/actions/productActions";
import EmptyCart from "../assets/image/emptycart.png";
import { SecondaryButton } from "../Global/Button/MuiButton";
import { useNavigate } from "react-router-dom";
import { Remove } from "@mui/icons-material";

const Wishlist = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, wishlist, error } =
    useSelector((state) => state.wishlist) || {};
  const isMobile = useMediaQuery("(max-width:767px)");

  const user = JSON.parse(localStorage.getItem("user"));
  const userId = user?._id;

  useEffect(() => {
    if (userId) {
      dispatch(GetWishlist(userId));
    }
  }, [dispatch, userId]);

  const handleAddToCart = (productId) => {
    if (!user) {
      alert("Please log in first");
      return;
    }
    dispatch(addToCart(user._id, productId, 1));
  };

  const handleRemove = (productId) => {
    dispatch(removeWishlist(productId, userId)).then(() => {
      dispatch(GetWishlist(userId));
    });
  };

  return (
    <Container>
      <Box sx={{ m: 2 }}>
        {loading && <CircularProgress />}
        {error && <Typography color="error">{error}</Typography>}

        {wishlist?.length > 0 ? (
          <>
            {isMobile ? (
              wishlist.map((item) => (
                <Card
                  key={item.productId}
                  sx={{
                    display: "flex",
                    mb: 2,
                    boxShadow: 2,
                    borderRadius: 2,
                    p: 1,
                  }}
                >
                  <Remove
                    onClick={() => handleRemove(item.productId._id)}
                    sx={{ cursor: "pointer", fontSize: "16px" }}
                  />
                  <img
                    src={item.ProductImage || EmptyCart}
                    alt={item.name || "No Image"}
                    style={{
                      height: "80px",
                      width: "80px",
                      borderRadius: "10px",
                      marginTop: "25px",
                    }}
                  />
                  <CardContent sx={{ flex: 1, p: 1 }}>
                    <Box
                      sx={{ display: "flex", justifyContent: "space-between" }}
                    >
                      <Typography variant="body1">
                        {item.name || "Unknown Product"}
                      </Typography>
                    </Box>
                    <Box
                      sx={{ display: "flex", justifyContent: "space-between" }}
                    >
                      <Typography variant="body2">Price:</Typography>
                      <Typography>₹{item.price || 0}</Typography>
                    </Box>
                    <Box
                      sx={{ display: "flex", justifyContent: "space-between" }}
                    >
                      <Typography variant="body2">Available:</Typography>
                      <Typography>
                        {item.Available ? "In stock" : "Out of stock"}
                      </Typography>
                    </Box>
                    <Box
                      sx={{ display: "flex", justifyContent: "space-between" }}
                    >
                      <SecondaryButton
                        text="ADD TO CART"
                        onClick={() => handleAddToCart(item.productId)}
                      />
                    </Box>
                  </CardContent>
                </Card>
              ))
            ) : (
              <TableContainer
                component={Paper}
                sx={{ overflowX: "auto", borderRadius: 2, boxShadow: 3 }}
              >
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell></TableCell>
                      <TableCell>Product</TableCell>
                      <TableCell align="right">Price</TableCell>
                      <TableCell align="right">Available</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {wishlist.map((item) => (
                      <TableRow key={item.productId}>
                        <TableCell>
                          <Remove
                            onClick={() => handleRemove(item.productId._id)}
                            sx={{ cursor: "pointer", fontSize: "16px" }}
                          />
                          <img
                            src={item.ProductImage || EmptyCart}
                            alt={item.name || "No Image"}
                            style={{
                              height: "80px",
                              width: "80px",
                              borderRadius: "10px",
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <Typography fontWeight="bold">
                            {item.name || "Unknown Product"}
                          </Typography>
                        </TableCell>
                        <TableCell align="right">₹{item.price || 0}</TableCell>
                        <TableCell align="right">
                          {item.Available ? "In stock" : "Out of stock"}
                        </TableCell>
                        <TableCell align="right">
                          <SecondaryButton
                            text="ADD TO CART"
                            onClick={() => handleAddToCart(item.productId)}
                          />
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            )}
          </>
        ) : (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              m: 2,
            }}
          >
            <img
              src={EmptyCart}
              alt="Empty Cart"
              style={{ width: "100px", height: "100px" }}
            />
            <Typography sx={{ fontSize: 20, fontWeight: 500, my: 1 }}>
              Your wishlist is empty.
            </Typography>
            <SecondaryButton
              text="START SHOPPING"
              onClick={() => navigate("/collection")}
            />
          </Box>
        )}
      </Box>
    </Container>
  );
};

export default Wishlist;
