import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getUserProfile } from "../../../redux/actions/productActions";
import {
  Avatar,
  Box,
  Container,
  Grid,
  Tab,
  Tabs,
  Typography,
} from "@mui/material";
import { Spin, Alert } from "antd";

const UserProfile = () => {
  const dispatch = useDispatch();
  const userProfile = useSelector((state) => state.userProfile);
  const { loading, error, data } = userProfile;
  console.log(data);

  useEffect(() => {
    dispatch(getUserProfile());
  }, [dispatch]);

  const [tabIndex, setTabIndex] = useState(0);
  const handleTabChange = (event, newIndex) => {
    setTabIndex(newIndex);
  };

  return (
    <Container>
      {loading && <Spin />}
      {error && (
        <Alert message="Error" description={error} type="error" showIcon />
      )}
      <Grid container spacing={2}>
        <Grid item xs={12} md={4} lg={3}>
          <Box
            sx={{
              p: 2,
              boxShadow: 2,
              borderRadius: "10px",
              textAlign: "center",
            }}
          >
            {data && (
              <>
                <Avatar
                  alt={data.user.username}
                  src={data.user.profilePicture}
                  sx={{ width: 100, height: 100, margin: "auto" }}
                />
                <Typography variant="h6">{data.user.username}</Typography>
                <Typography variant="body2">{data.user.profession}</Typography>
                <Typography variant="body2">
                  {data.user.address["street"]}
                </Typography>
              </>
            )}
          </Box>
        </Grid>

        <Grid item xs={12} md={8} lg={9}>
          <Tabs
            value={tabIndex}
            onChange={handleTabChange}
            variant="scrollable"
            scrollButtons="auto"
          >
            <Tab label="Dashboard" />
            <Tab label="Orders" />
            <Tab label="Address" />
          </Tabs>

          <Box sx={{ p: 2, boxShadow: 2, borderRadius: "10px", mt: 2 }}>
            {tabIndex === 0 && (
              <Typography variant="body1">
                Welcome to your Dashboard!
              </Typography>
            )}
            {tabIndex === 1 && (
              <Typography variant="body1">Here are your orders.</Typography>
            )}
            {tabIndex === 2 && (
              <Typography variant="body1">Manage your address here.</Typography>
            )}
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default UserProfile;
