import React from "react";
import { useLocation } from "react-router-dom";
import { Container, Typography, Grid, Box } from "@mui/material";

// Format Date
const formatDate = (date) => {
  return new Date(date).toLocaleDateString("en-US", {
    year: "numeric",
    month: "long",
    day: "numeric",
  });
};

const OrderDetails = () => {
  const location = useLocation();
  const orders= location.state?.order;
  return (
    <Container>
      {orders ? (
        <Box>
          <Typography variant="h4">Order Details</Typography>
          <Typography variant="h6">Order ID: {orders._id}</Typography>
          <Typography>Date: {formatDate(orders.createdAt)}</Typography>
          <Typography>Status: {orders.status}</Typography>
          <Typography>Total: ₹{orders.amount}</Typography>
          <Grid container spacing={3} sx={{ marginTop: 2 }}>
            {orders.items.map((item, index) => (
              <Box
                sx={{
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-between",
                }}
              >
                <Box>
                  <Typography>Product: {item.productDetails?.name}</Typography>
                  <Typography>Price: ₹{item.productDetails?.price}</Typography>
                </Box>
                <Box>
                  <img
                    src={item.productDetails?.productImage}
                    alt={item.productDetails?.name}
                    style={{
                      height: "60px",
                      width: "60px",
                      borderRadius: "10px",
                    }}
                  />
                </Box>
              </Box>
            ))}
          </Grid>
        </Box>
      ) : (
        <Typography>No order found</Typography>
      )}
    </Container>
  );
};

export default OrderDetails;
