import React from "react";
import { useLocation } from "react-router-dom";
import {
  Container,
  Typography,
  Grid,
  Box,
  TableRow,
  Table,
  TableHead,
  TableCell,
} from "@mui/material";
import theme from "../../../../theme/theme";

// Format Date
const formatDate = (date) => {
  return new Date(date).toLocaleDateString("en-US", {
    year: "numeric",
    month: "long",
    day: "numeric",
  });
};

const OrderDetails = () => {
  const location = useLocation();
  const orders = location.state?.order;

  return (
    <Container>
      <Typography>
        Order was placed {formatDate(orders.createdAt)} and is currently
        <span style={{ color: theme.palette.primary.main, marginLeft: "10px" }}>
          {" "}
          {orders.status}
        </span>
        .
      </Typography>
      {orders ? (
        <Box>
          <Table>
            <TableRow>
              <TableHead>
                <TableCell>Product</TableCell>
              </TableHead>
            </TableRow>
            <TableRow>
              <TableHead>
                <TableCell>
                  {orders.items.map((item, index) => (
                    <Box
                      sx={{
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "space-between",
                      }}
                    >
                      <Box>
                        <img
                          src={item.productDetails?.productImage}
                          alt={item.productDetails?.name}
                          style={{
                            height: "60px",
                            width: "60px",
                            borderRadius: "10px",
                          }}
                        />
                      </Box>
                      <Box>
                        <Typography>
                          Product: {item.productDetails?.name}
                        </Typography>
                        <Typography>
                          Price: ₹{item.productDetails?.price}
                        </Typography>
                      </Box>
                    </Box>
                  ))}
                </TableCell>
              </TableHead>
            </TableRow>
            <TableRow>
              <TableHead>
                <TableCell>Subtotal:</TableCell>
                <TableCell>{orders.amount}</TableCell>
              </TableHead>
            </TableRow>
            <TableRow>
              <TableHead>
                <TableCell>Payment Method</TableCell>
                <TableCell>{orders.paymentMethod}</TableCell>
              </TableHead>
            </TableRow>
            <TableRow>
              <TableHead>
                <TableCell>Total</TableCell>
                <TableCell>₹{orders.amount}</TableCell>
              </TableHead>
            </TableRow>
            <TableRow></TableRow>
          </Table>
        </Box>
      ) : (
        <Typography>No order found</Typography>
      )}
    </Container>
  );
};

export default OrderDetails;
