import React, { useEffect } from "react";
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  IconButton,
  TextField,
  Button,
  Divider,
  Container,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import { styled } from "@mui/system";
import { useDispatch, useSelector } from "react-redux";
import { GetAddToCart } from "../redux/actions/productActions";

// Styled components
const StyledCard = styled(Card)(({ theme }) => ({
  display: "flex",
  marginBottom: theme.spacing(2),
  boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
}));

const StyledCardMedia = styled(CardMedia)(({ theme }) => ({
  width: 150,
  height: 150,
  objectFit: "cover",
  [theme.breakpoints.down("sm")]: {
    width: 100,
    height: 100,
  },
}));

const Cart = () => {
  const dispatch = useDispatch();
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

  // Fetch cartItems from Redux store
  const { cartItems, loading, error } = useSelector((state) => state.cart);

  // Fetch cart items when the component mounts
  useEffect(() => {
    dispatch(GetAddToCart());
  }, [dispatch]);

  // Handle increment quantity
  const handleIncrement = (index) => {
    const updatedItems = [...cartItems];
    updatedItems[index].quantity += 1;
    // Dispatch an action to update the quantity in the backend (if needed)
    // dispatch(UpdateCartItem(updatedItems[index]));
  };

  // Handle decrement quantity
  const handleDecrement = (index) => {
    const updatedItems = [...cartItems];
    if (updatedItems[index].quantity > 1) {
      updatedItems[index].quantity -= 1;
      // Dispatch an action to update the quantity in the backend (if needed)
      // dispatch(UpdateCartItem(updatedItems[index]));
    }
  };

  // Handle delete item
  const handleDelete = (index) => {
    const itemToDelete = cartItems[index];
    // Dispatch an action to delete the item from the backend
    // dispatch(RemoveFromCart(itemToDelete.productId));
  };

  // Calculate subtotal
  const subtotal = cartItems.reduce(
    (total, item) => total + item.product.price * item.quantity,
    0
  );

  // Shipping fees
  const shippingFees = 5.99;

  // Total
  const total = subtotal + shippingFees;

  if (loading) {
    return <Typography>Loading...</Typography>;
  }

  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        Your Cart
      </Typography>
      <Grid container spacing={4}>
        {/* Cart Items */}
        <Grid item xs={12} md={8}>
          {cartItems.map((item, index) => (
            <StyledCard key={item.productId}>
              <StyledCardMedia
                image={item.product.ProductImage}
                alt={item.product.name}
              />
              <CardContent sx={{ flex: 1 }}>
                <Typography variant="h6">{item.product.name}</Typography>
                <Typography variant="body1" color="textSecondary">
                  &#8377;{item.product.price.toFixed(2)}
                </Typography>
                <Box sx={{ display: "flex", alignItems: "center", mt: 1 }}>
                  <TextField
                    type="number"
                    value={item.quantity}
                    size="small"
                    sx={{ width: 80, mr: 2 }}
                    inputProps={{ min: 1 }}
                    onChange={(e) => {
                      const updatedItems = [...cartItems];
                      updatedItems[index].quantity = parseInt(e.target.value, 10);
                      // Dispatch an action to update the quantity in the backend (if needed)
                      // dispatch(UpdateCartItem(updatedItems[index]));
                    }}
                  />
                  <IconButton
                    color="error"
                    onClick={() => handleDelete(index)}
                  >
                    <DeleteIcon />
                  </IconButton>
                  <IconButton onClick={() => handleIncrement(index)}>
                    +
                  </IconButton>
                  <IconButton onClick={() => handleDecrement(index)}>
                    -
                  </IconButton>
                </Box>
              </CardContent>
            </StyledCard>
          ))}
        </Grid>

        {/* Order Summary */}
        <Grid item xs={12} md={4}>
          <Card sx={{ boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)" }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Order Summary
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Box
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    mb: 1,
                  }}
                >
                  <Typography variant="body1">Subtotal</Typography>
                  <Typography variant="body1">
                    &#8377;{subtotal.toFixed(2)}
                  </Typography>
                </Box>
                <Box
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    mb: 1,
                  }}
                >
                  <Typography variant="body1">Shipping</Typography>
                  <Typography variant="body1">
                    &#8377;{shippingFees.toFixed(2)}
                  </Typography>
                </Box>
                <Divider sx={{ my: 2 }} />
                <Box
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    mb: 2,
                  }}
                >
                  <Typography variant="body1" fontWeight="bold">
                    Total
                  </Typography>
                  <Typography variant="body1" fontWeight="bold">
                    &#8377;{total.toFixed(2)}
                  </Typography>
                </Box>
                <Button
                  variant="contained"
                  fullWidth
                  size="large"
                  sx={{ mt: 2 }}
                  onClick={() => alert("Proceeding to checkout...")}
                >
                  Proceed to Checkout
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Cart;
