import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUserOrders } from '../../../redux/actions/productActions';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Chip,
  CircularProgress,
  Alert,
} from '@mui/material';

const OrderStatus = () => {
  const dispatch = useDispatch();
  const userOrders = useSelector((state) => state.userOrders);
  const { loading, orders, error } = userOrders;

  useEffect(() => {
    dispatch(getUserOrders());
  }, [dispatch]);

  // Function to get chip color based on order status
  const getStatusChipColor = (status) => {
    switch (status) {
      case 'Pending':
        return 'default';
      case 'Shipped':
        return 'primary';
      case 'Delivered':
        return '#6E8E59';
      case 'Cancelled':
        return '#BE3144';
      default:
        return 'default';
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>User Orders</Typography>
      {loading && <CircularProgress />}
      {error && <Alert severity="error">{error}</Alert>}
      {orders && orders.length > 0 ? (
        <Grid container spacing={3}>
          {orders.map((order) => (
            <Grid item xs={12} sm={6} md={4} key={order._id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">Order ID: {order._id}</Typography>
                  <Typography>Amount: ₹{order.amount}</Typography>
                  <Typography>Payment Method: {order.paymentMethod}</Typography>
                  <Chip
                    label={order.status}
                    color={getStatusChipColor(order.status)}
                    style={{ marginTop: 8 }}
                  />
                  <Typography variant="subtitle1" style={{ marginTop: 16 }}>Items:</Typography>
                  <ul>
                    {order.items.map((item, index) => (
                      <li key={index}>
                        <Typography>Product: {item.productDetails?.name}</Typography>
                        <Typography>Price: ₹{item.productDetails?.price}</Typography>
                        <Typography>Quantity: {item.quantity}</Typography>
                      </li>
                    ))}
                  </ul>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      ) : (
        <Typography>No orders found</Typography>
      )}
    </Container>
  );
};

export default OrderStatus;
