import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getUserOrders } from "../../../redux/actions/productActions";
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Chip,
  CircularProgress,
  Alert,
  Box,
  Badge,
} from "@mui/material";
import theme from "../../../theme/theme";

const OrderStatus = () => {
  const dispatch = useDispatch();
  const userOrders = useSelector((state) => state.userOrders);
  const { loading, orders, error } = userOrders;

  useEffect(() => {
    dispatch(getUserOrders());
  }, [dispatch]);

  // Function to get chip color based on order status
  const getStatusChipColor = (status) => {
    switch (status) {
      case "Pending":
        return "#213555";
      case "Shipped":
        return "primary";
      case "Delivered":
        return "#6E8E59";
      case "Cancelled":
        return "#BE3144";
      default:
        return "default";
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        User Orders
      </Typography>
      {loading && <CircularProgress />}
      {error && <Alert severity="error">{error}</Alert>}
      {orders && orders.length > 0 ? (
        <Grid container spacing={3}>
          {orders.map((order) => (
            <Grid item xs={12} sm={6} md={4} key={order._id}>
              <Card>
                <Box
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems:"center"
                  }}
                >
                    <Box>₹{order.amount}</Box>
                    <Box>{order.paymentMethod}</Box>
                    <Box>
                      <Chip
                        label={order.status}
                        color={getStatusChipColor(order.status)}
                        style={{
                          marginTop: 8,
                          padding: 1,
                          color: theme.palette.white.main,
                        }}
                      />
                  </Box>
                </Box>

                <ul>
                  {order.items.map((item, index) => (
                    <li key={index}>
                      <Box
                        sx={{
                          display: "flex",
                          justifyContent: "space-between",
                        }}
                      >
                        <Box>
                          <Badge badgeContent={item.quantity} color="primary">
                            <img
                              src={item.productDetails?.productImage}
                              alt={item.productDetails?.name}
                              style={{
                                height: "60px",
                                width: "60px",
                                borderRadius: "10px",
                              }}
                            />
                          </Badge>
                        </Box>
                        <Box>
                          <Typography>
                            Product: {item.productDetails?.name}
                          </Typography>
                          <Typography>
                            Price: ₹{item.productDetails?.price}
                          </Typography>
                        </Box>
                      </Box>
                    </li>
                  ))}
                </ul>
              </Card>
            </Grid>
          ))}
        </Grid>
      ) : (
        <Typography>No orders found</Typography>
      )}
    </Container>
  );
};

export default OrderStatus;
