import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { GetAddToCart } from "../redux/actions/productActions";

function Cart() {
  const dispatch = useDispatch();
  const cartList = useSelector((state) => state.cartList);
  const { loading, cart, error } = cartList || {};
  console.log(cart);
  

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user && user._id) {
      dispatch(GetAddToCart(user._id));
    }
  }, [dispatch]);

  useEffect(() => {
    if (data) {
      toast.success(data);
    } else if (error) {
      toast.error(error);
    }
    // eslint-disable-next-line
  }, [error, data]);


  return (
    <div>
      <h2>Cart Items</h2>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {cart?.items?.length > 0 ? (
        <ul>
          {cart.items.map((item) => (
            <li key={item.productId._id}>
              <img src={item.productId.image} alt={item.productId.name} width="50" />
              {item.productId.name} - ${item.productId.price} x {item.quantity}
            </li>
          ))}
        </ul>
      ) : (
        <p>No items in cart.</p>
      )}
      <h3>Total: ${cart.totalPrice.toFixed(2)}</h3>
    </div>
  );
}

export default Cart;
