import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Grid,
  Button,
  CircularProgress,
  Box,
} from "@mui/material";
import { GetAddToCart, removeFromCart, addToCart } from "../redux/actions/productActions";

function Cart() {
  const dispatch = useDispatch();
  const cartList = useSelector((state) => state.cartList);
  const { loading, cart, error } = cartList.cart || {};

  const user = JSON.parse(localStorage.getItem("user"));
  const userId = user?._id;

  useEffect(() => {
    if (userId) {
      dispatch(GetAddToCart(userId));
    }
  }, [dispatch, userId]);

  const handleRemove = (productId) => {
    dispatch(removeFromCart(productId, userId));
  };

  const handleAdd = (product) => {
    dispatch(addToCart(product, userId));
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Shopping Cart
      </Typography>
      {loading && <CircularProgress />}
      {error && <Typography color="error">{error}</Typography>}
      {cart?.items?.length > 0 ? (
        <Grid container spacing={2}>
          {cart.items.map((item) => (
            <Grid item xs={12} sm={6} md={4} key={item.productId._id}>
              <Card>
                <CardMedia
                  component="img"
                  height="200"
                  image={item.productId.ProductImage}
                  alt={item.productId.name}
                />
                <CardContent>
                  <Typography variant="h6">{item.productId.name}</Typography>
                  <Typography variant="body1">
                    ${item.productId.price} x {item.quantity}
                  </Typography>
                  <Box display="flex" justifyContent="space-between" mt={2}>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={() => handleAdd(item.productId)}
                    >
                      Add More
                    </Button>
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => handleRemove(item.productId._id)}
                    >
                      Remove
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      ) : (
        <Typography>No items in cart.</Typography>
      )}
    </Container>
  );
}

export default Cart;
