import React, { useEffect } from "react";  
import { useDispatch, useSelector } from "react-redux";  
import {  
  Container,  
  Typography,  
  Chip,  
  CircularProgress,  
  Alert,  
  Table,  
  TableHead,  
  TableRow,  
  TableCell,  
  TableBody,  
  Box,  
  CardContent,  
  useMediaQuery,  
  useTheme,  
  Grid,  
} from "@mui/material";  
import OutboundIcon from "@mui/icons-material/Outbound";  
import { getUserOrders, getOrderDetails } from "../../../../redux/actions/productActions";  

// Function to get chip color based on order status  
const getStatusChipColor = (status) => {  
  switch (status) {  
    case "Pending":  
      return "default";  
    case "Shipped":  
      return "primary";  
    case "Delivered":  
      return "success";  
    case "Cancelled":  
      return "error";  
    default:  
      return "default";  
  }  
};  

// Format Date  
const formatDate = (date) => {  
  return new Date(date).toLocaleDateString("en-US", {  
    year: "numeric",  
    month: "long",  
    day: "numeric",  
  });  
};  

const OrderStatus = ({ selectedOrderId, onOrderSelect }) => {  
  const dispatch = useDispatch();  
  const userOrders = useSelector((state) => state.userOrders);  
  const orderDetails = useSelector((state) => state.orderDetails);  
  const { loading: loadingOrders, orders, error: errorOrders } = userOrders;  
  const { loading: loadingOrderDetails, order, error: errorOrderDetails } = orderDetails;  
  const theme = useTheme();  
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));  

  useEffect(() => {  
    dispatch(getUserOrders());  
  }, [dispatch]);  

  useEffect(() => {  
    if (selectedOrderId) {  
      dispatch(getOrderDetails(selectedOrderId));  
    }  
  }, [dispatch, selectedOrderId]);  

  return (  
    <Container>  
      {loadingOrders && <CircularProgress />}  
      {errorOrders && <Alert severity="error">{errorOrders}</Alert>}  
      {selectedOrderId ? (  
        loadingOrderDetails ? (  
          <CircularProgress />  
        ) : errorOrderDetails ? (  
          <Alert severity="error">{errorOrderDetails}</Alert>  
        ) : (  
          order && (  
            <Box>  
              <Typography variant="h4">Order Details</Typography>  
              <Typography variant="h6">Order ID: {order._id}</Typography>  
              <Typography>Date: {formatDate(order.createdAt)}</Typography>  
              <Typography>Status: {order.status}</Typography>  
              <Typography>Total: ₹{order.amount}</Typography>  
              <Grid container spacing={3} sx={{ marginTop: 2 }}>  
                {order.items.map((item) => (  
                  <Grid item xs={12} md={6} key={item.productDetails?.id}>  
                    <Box  
                      sx={{  
                        display: "flex",  
                        alignItems: "center",  
                        justifyContent: "space-between",  
                      }}  
                    >  
                      <Box>  
                        <Typography>Product: {item.productDetails?.name}</Typography>  
                        <Typography>Price: ₹{item.productDetails?.price}</Typography>  
                        <Typography>Quantity: {item.quantity}</Typography>  
                      </Box>  
                      <Box>  
                        <img  
                          src={item.productDetails?.productImage}  
                          alt={item.productDetails?.name}  
                          style={{ height: "60px", width: "60px", borderRadius: "10px" }}  
                        />  
                      </Box>  
                    </Box>  
                  </Grid>  
                ))}  
              </Grid>  
            </Box>  
          )  
        )  
      ) : (  
        orders && orders.length > 0 ? (  
          isMobile ? (  
            <Grid container spacing={3}>  
              {orders.map((order, index) => (  
                <Grid item xs={12} key={order._id}>  
                  <Box>  
                    <CardContent>  
                      <Box  
                        sx={{  
                          display: "flex",  
                          justifyContent: "space-between",  
                        }}  
                      >  
                        <Box>  
                          <Typography variant="h6">Order: {index + 1}</Typography>  
                          <Typography>Date: {formatDate(order.createdAt)}</Typography>  
                        </Box>  
                        <Box>  
                          <Chip  
                            size="small"  
                            label={order.status}  
                            color={getStatusChipColor(order.status)}  
                          />  
                        </Box>  
                      </Box>  

                      <Box  
                        sx={{  
                          display: "flex",  
                          justifyContent: "space-between",  
                        }}  
                      >  
                        <Typography>  
                          Total: ₹{order.amount}  
                          <span style={{ color: theme.palette.primary.main, marginLeft: "10px" }}>  
                            {order.items.length} Items  
                          </span>  
                        </Typography>  
                        <OutboundIcon  
                          style={{ cursor: "pointer" }}  
                          onClick={() => onOrderSelect(order._id)}  
                        />  
                      </Box>  
                    </CardContent>  
                  </Box>  
                </Grid>  
              ))}  
            </Grid>  
          ) : (  
            <Box sx={{ overflowX: "auto" }}>  
              <Table>  
                <TableHead>  
                  <TableRow>  
                    <TableCell>Order</TableCell>  
                    <TableCell>Date</TableCell>  
                    <TableCell>Status</TableCell>  
                    <TableCell align="center">Total</TableCell>  
                    <TableCell align="center">Action</TableCell>  
                  </TableRow>  
                </TableHead>  
                <TableBody>  
                  {orders.map((order, index) => (  
                    <TableRow key={order._id}>  
                      <TableCell>{index + 1}</TableCell>  
                      <TableCell>{formatDate(order.createdAt)}</TableCell>  
                      <TableCell>  
                        <Chip  
                          label={order.status}  
                          color={getStatusChipColor(order.status)}  
                        />  
                      </TableCell>  
                      <TableCell align="center">  
                        <Typography>₹{order.amount}</Typography>{" "}  
                        {order.items.length} Items  
                      </TableCell>  
                      <TableCell align="center">  
                        <OutboundIcon  
                          style={{ cursor: "pointer" }}  
                          onClick={() => onOrderSelect(order._id)} // Fixed onClick handler  
                        />  
                      </TableCell>  
                    </TableRow>  
                  ))}  
                </TableBody>  
              </Table>  
            </Box>  
          )  
        ) : (  
          <Typography>No orders found</Typography>  
        )  
      )}  
    </Container>  
  );  
};  

export default OrderStatus;
