import React, { useState } from "react";
import { Box, Container, Grid, TextField, Typography, Button, RadioGroup, FormControlLabel, Radio, FormLabel, FormControl } from "@mui/material";
import axios from "axios";
import theme from "../../../theme/theme";

function Checkout() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    phone: "",
    email: "",
    zipcode: "",
    country: "",
    state: "",
    city: "",
    userId: "", // Assuming you get the userId from your authentication logic
    items: [], // Assuming you get the items from your cart logic
    amount: 0, // Assuming you calculate the amount from your cart logic
    address: "",
    paymentMethod: "COD",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handlePaymentMethodChange = (e) => {
    setFormData({ ...formData, paymentMethod: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`, // Assuming you store the token in local storage
        },
      };

      const { data } = await axios.post("/api/orders", formData, config);
      console.log("Order created successfully:", data);
      // Handle success (e.g., redirect to a success page, clear cart, etc.)
    } catch (error) {
      console.error("Error creating order:", error);
      // Handle error (e.g., display error message)
    }
  };

  return (
    <Box sx={{ mt: 2 }}>
      <Container>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6} lg={8}>
            <Typography
              variant="h6"
              sx={{
                fontWeight: "bold",
              }}
            >
              Billing details
            </Typography>
            <Box
              sx={{
                boxShadow: 3,
                border: `1px solid ${theme.palette.lightgrey.main}`,
                p: 2,
              }}
            >
              <form onSubmit={handleSubmit}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      label="First Name"
                      name="firstName"
                      size="small"
                      value={formData.firstName}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      label="Last Name"
                      name="lastName"
                      size="small"
                      value={formData.lastName}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      label="Phone"
                      name="phone"
                      size="small"
                      value={formData.phone}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      label="Email"
                      name="email"
                      size="small"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={4}>
                    <TextField
                      fullWidth
                      label="Zipcode"
                      name="zipcode"
                      size="small"
                      value={formData.zipcode}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={4}>
                    <TextField
                      fullWidth
                      label="Country"
                      name="country"
                      size="small"
                      value={formData.country}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={4}>
                    <TextField
                      fullWidth
                      label="State"
                      name="state"
                      size="small"
                      value={formData.state}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={4}>
                    <TextField
                      fullWidth
                      label="City"
                      name="city"
                      size="small"
                      value={formData.city}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Address"
                      name="address"
                      size="small"
                      value={formData.address}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <FormControl component="fieldset">
                      <FormLabel component="legend">Payment Method</FormLabel>
                      <RadioGroup
                        aria-label="paymentMethod"
                        name="paymentMethod"
                        value={formData.paymentMethod}
                        onChange={handlePaymentMethodChange}
                      >
                        <FormControlLabel
                          value="COD"
                          control={<Radio />}
                          label="Cash on Delivery"
                        />
                        <FormControlLabel
                          value="Razorpay"
                          control={<Radio />}
                          label="Razorpay"
                        />
                      </RadioGroup>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12}>
                    <Button type="submit" variant="contained" color="primary">
                      Place Order
                    </Button>
                  </Grid>
                </Grid>
              </form>
            </Box>
          </Grid>
          <Grid item xs={12} md={6} lg={4}>
            {/* Add any additional content for the right column here */}
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
}

export default Checkout;
