import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Grid,
  Rating,
} from "@mui/material";
import { SecondaryButton } from "../Global/Button/MuiButton";

const ProductReview = ({ productId }) => {
  const [comment, setComment] = useState("");
  const [rating, setRating] = useState(0);

  const handleCommentChange = (e) => {
    setComment(e.target.value);
  };

  const handleRatingChange = (e, newValue) => {
    setRating(newValue);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const review = {
      comment,
      rating,
      userId: "67a2b800d712492b8fb52b0f", // Mock user ID, replace with actual user ID
      productId,
    };

    // Send review to the backend (Replace with actual API call)
    console.log("Review submitted:", review);
  };

  return (
    <Container>
      <Typography variant="h6">Add a Review</Typography>
      <form onSubmit={handleSubmit}>
        <Box my={2}>
          <Rating
            name="rating"
            value={rating}
            onChange={handleRatingChange}
            precision={0.5}
          />
        </Box>
        <Box my={2}>
          <TextField
            label="Comment"
            multiline
            fullWidth
            rows={4}
            variant="outlined"
            value={comment}
            onChange={handleCommentChange}
          />
        </Box>
        <Box my={2}>
          <SecondaryButton text="Submit Review"/>
            
        </Box>
      </form>
    </Container>
  );
};

export default ProductReview;
