import React, { useEffect } from "react";
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  IconButton,
  TextField,
  Button,
  Divider,
  Container,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import { styled } from "@mui/system";
import { useDispatch, useSelector } from "react-redux";
import { GetAddToCart } from "../redux/actions/productActions";

// Styled components
const StyledCard = styled(Card)(({ theme }) => ({
  display: "flex",
  marginBottom: theme.spacing(2),
  boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
}));

const StyledCardMedia = styled(CardMedia)(({ theme }) => ({
  width: 150,
  height: 150,
  objectFit: "cover",
  [theme.breakpoints.down("sm")]: {
    width: 100,
    height: 100,
  },
}));

const Cart = () => {
  const dispatch = useDispatch();
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

  // Fetch cartItems from Redux sto re
  const { cartItems, loading, error } = useSelector((state) => state.cart);
  const user = JSON.parse(localStorage.getItem("user"));
  useEffect(() => {
    const userId = user._id; 
    console.log(userId);
    
    dispatch(GetAddToCart(userId));
  }, [dispatch]);
  
  if (loading) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">{error}</Typography>;

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        Your Cart
      </Typography>
      <Grid container spacing={4}>
        <Grid item xs={12} md={8}>
          {cartItems.map((item) => (
            <StyledCard key={item.productId}>
              <StyledCardMedia image={item.product.ProductImage} alt={item.product.name} />
              <CardContent sx={{ flex: 1 }}>
                <Typography variant="h6">{item.product.name}</Typography>
                <Typography variant="body1" color="textSecondary">
                  &#8377;{item.product.price.toFixed(2)}
                </Typography>
                <Box sx={{ display: "flex", alignItems: "center", mt: 1 }}>
                  <TextField type="number" value={item.quantity} size="small" sx={{ width: 80, mr: 2 }} inputProps={{ min: 1 }} />
                  <IconButton color="error">
                    <DeleteIcon />
                  </IconButton>
                </Box>
              </CardContent>
            </StyledCard>
          ))}
        </Grid>
        <Grid item xs={12} md={4}>
          <Card sx={{ boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)" }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Order Summary
              </Typography>
              <Divider sx={{ my: 2 }} />
              <Button variant="contained" fullWidth size="large">
                Proceed to Checkout
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Cart;
