import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Grid,
  Button,
  CircularProgress,
  Box,
  IconButton,
} from "@mui/material";
import { Add, Remove } from "@mui/icons-material";
import { GetAddToCart, removeFromCart, addToCart } from "../redux/actions/productActions";

function Cart() {
  const dispatch = useDispatch();
  const cartList = useSelector((state) => state.cartList);
  const { loading, cart, error } = cartList.cart || {};

  const user = JSON.parse(localStorage.getItem("user"));
  const userId = user?._id;

  useEffect(() => {
    if (userId) {
      dispatch(GetAddToCart(userId));
    }
  }, [dispatch, userId]);

  const handleRemove = (productId) => {
    dispatch(removeFromCart(productId, userId));
  };

  const handleIncrease = (productId, quantity) => {
    dispatch(addToCart(userId, productId, quantity + 1));
  };

  const handleDecrease = (productId, quantity) => {
    if (quantity > 1) {
      dispatch(addToCart(userId, productId, quantity - 1));
    } else {
      dispatch(removeFromCart(productId, userId));
    }
  };

  const calculateTotalPrice = () => {
    return cart?.items?.reduce((total, item) => total + item.productId.price * item.quantity, 0) || 0;
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Shopping Cart
      </Typography>
      {loading && <CircularProgress />}
      {error && <Typography color="error">{error}</Typography>}
      {cart?.items?.length > 0 ? (
        <>
          <Grid container spacing={2}>
            {cart.items.map((item) => (
              <Grid item xs={12} sm={6} md={4} key={item.productId._id}>
                <Card>
                  <CardMedia component="img" height="200" image={item.productId.ProductImage} alt={item.productId.name} />
                  <CardContent>
                    <Typography variant="h6">{item.productId.name}</Typography>
                    <Typography variant="body1">${item.productId.price} x {item.quantity}</Typography>
                    <Box display="flex" alignItems="center" justifyContent="space-between" mt={2}>
                      <IconButton onClick={() => handleDecrease(item.productId._id, item.quantity)}>
                        <Remove />
                      </IconButton>
                      <Typography>{item.quantity}</Typography>
                      <IconButton onClick={() => handleIncrease(item.productId._id, item.quantity)}>
                        <Add />
                      </IconButton>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
          <Box mt={4} p={2} bgcolor="#f5f5f5" borderRadius={2}>
            <Typography variant="h5" align="right">Total Price: ${calculateTotalPrice().toFixed(2)}</Typography>
          </Box>
        </>
      ) : (
        <Typography>No items in cart.</Typography>
      )}
    </Container>
  );
}

export default Cart;
