import React from "react";
import {
  Box,
  Container,
  Grid,
  TextField,
  Typography,
  Button,
  RadioGroup,
  FormControlLabel,
  Radio,
  FormLabel,
  FormControl,
  Divider,
  Card,
  CardContent,
  Badge,
} from "@mui/material";
import axios from "axios";
import theme from "../../../theme/theme";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import { useCart } from "../../../Context/CartContext";

const CheckoutSchema = Yup.object().shape({
  firstName: Yup.string().required("First Name is required"),
  lastName: Yup.string().required("Last Name is required"),
  phone: Yup.string().required("Phone is required"),
  email: Yup.string().email("Invalid email").required("Email is required"),
  zipcode: Yup.string().required("Zipcode is required"),
  country: Yup.string().required("Country is required"),
  state: Yup.string().required("State is required"),
  city: Yup.string().required("City is required"),
  address: Yup.string().required("Address is required"),
  paymentMethod: Yup.string().required("Payment Method is required"),
});

const Checkout = () => {
  const { cart } = useCart();
  
  const calculateTotalPrice = () => {
    return (
      cart.cart?.items?.reduce(
        (total, item) => total + item.productId.price * item.quantity,
        0
      ) || 0
    );
  };


  const initialValues = {
    firstName: "",
    lastName: "",
    phone: "",
    email: "",
    zipcode: "",
    country: "",
    state: "",
    city: "",
    userId: "",
    items: [],
    amount: 0,
    address: "",
    paymentMethod: "COD",
  };

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      };

      const { data } = await axios.post("/api/orders", values, config);
      console.log("Order created successfully:", data);
    } catch (error) {
      console.error("Error creating order:", error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Box sx={{ mt: 2 }}>
      <Container>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6} lg={8}>
            <Typography
              variant="h6"
              sx={{
                fontWeight: "bold",
              }}
            >
              Billing details
            </Typography>
            <Box
              sx={{
                boxShadow: 3,
                border: `1px solid ${theme.palette.lightgrey.main}`,
                p: 2,
              }}
            >
              <Formik
                initialValues={initialValues}
                validationSchema={CheckoutSchema}
                onSubmit={handleSubmit}
              >
                {({ errors, touched, isSubmitting }) => (
                  <Form>
                    <Grid container spacing={3}>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="First Name"
                          name="firstName"
                          size="small"
                          error={touched.firstName && Boolean(errors.firstName)}
                          helperText={touched.firstName && errors.firstName}
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Last Name"
                          name="lastName"
                          size="small"
                          error={touched.lastName && Boolean(errors.lastName)}
                          helperText={touched.lastName && errors.lastName}
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6} lg={4}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Phone"
                          name="phone"
                          size="small"
                          error={touched.phone && Boolean(errors.phone)}
                          helperText={touched.phone && errors.phone}
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6} lg={4}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Email"
                          name="email"
                          size="small"
                          error={touched.email && Boolean(errors.email)}
                          helperText={touched.email && errors.email}
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6} lg={4}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Zipcode"
                          name="zipcode"
                          size="small"
                          error={touched.zipcode && Boolean(errors.zipcode)}
                          helperText={touched.zipcode && errors.zipcode}
                          required
                        />
                      </Grid>
                      <Grid item item xs={12} md={6} lg={4}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Country"
                          name="country"
                          size="small"
                          error={touched.country && Boolean(errors.country)}
                          helperText={touched.country && errors.country}
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6} lg={4}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="State"
                          name="state"
                          size="small"
                          error={touched.state && Boolean(errors.state)}
                          helperText={touched.state && errors.state}
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6} lg={4}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="City"
                          name="city"
                          size="small"
                          error={touched.city && Boolean(errors.city)}
                          helperText={touched.city && errors.city}
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6} lg={4}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Address"
                          name="address"
                          size="small"
                          error={touched.address && Boolean(errors.address)}
                          helperText={touched.address && errors.address}
                          required
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <FormControl
                          component="fieldset"
                          error={
                            touched.paymentMethod &&
                            Boolean(errors.paymentMethod)
                          }
                        >
                          <FormLabel component="legend">
                            Payment Method
                          </FormLabel>
                          <RadioGroup
                            aria-label="paymentMethod"
                            name="paymentMethod"
                          >
                            <FormControlLabel
                              value="COD"
                              control={<Radio />}
                              label="Cash on Delivery"
                            />
                            <FormControlLabel
                              value="Razorpay"
                              control={<Radio />}
                              label="Razorpay"
                            />
                          </RadioGroup>
                          {touched.paymentMethod && errors.paymentMethod && (
                            <Typography color="error">
                              {errors.paymentMethod}
                            </Typography>
                          )}
                        </FormControl>
                      </Grid>
                      <Grid item xs={12}>
                        <Button
                          type="submit"
                          variant="contained"
                          color="primary"
                          disabled={isSubmitting}
                        >
                          Place Order
                        </Button>
                      </Grid>
                    </Grid>
                  </Form>
                )}
              </Formik>
            </Box>
          </Grid>
          <Grid item xs={12} md={6} lg={4}>
            <Box
              sx={{
                boxShadow: 3,
                p: 1,
                border: `1px solid ${theme.palette.lightgrey.main}`,
                borderRadius: 3,
              }}
            >
              <Typography variant="h6">Your Order</Typography>
              <Divider sx={{ my: 2 }} />
              <Typography>Product</Typography>
              {cart.cart?.items.map((item) => (
                <>
                  <Box
                    sx={{
                      display: "flex",
                      flexDirection: "row",
                      justifyContent: "space-between",
                      alignItems: "center",
                    }}
                  >
                    <Box
                      key={item.productId._id}
                      sx={{
                        display: "flex",
                        borderRadius: 2,
                        p: 1,
                      }}
                    >
                      <Badge badgeContent={item.quantity} color="primary">
                        <img
                          src={item.productId.ProductImage}
                          alt={item.productId.name}
                          style={{
                            height: "60px",
                            width: "60px",
                            borderRadius: "10px",
                          }}
                        />
                      </Badge>
                    </Box>
                    <Box>
                      <Box
                        sx={{
                          display: "flex",
                          flexDirection: "row",
                          justifyContent: "flex-start",
                          alignItems: "center",
                        }}
                      >
                        <Typography variant="body1">
                          {item.productId.name}
                        </Typography>

                        <Typography>₹{item.productId.price}</Typography>
                      </Box>
                    </Box>
                  </Box>
                </>
              ))}
              <Divider sx={{ my: 2 }} />
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <Box>
                  <b>Total: </b>
                </Box>
                <Box>₹{calculateTotalPrice().toFixed(2)}</Box>
              </Box>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Checkout;
