import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  Box,
  Grid,
  CircularProgress,
  Avatar,
} from "@mui/material";
import Rating from "@mui/material/Rating";
import { getProductReviews } from "../../redux/actions/productActions";
import { format } from 'date-fns';

const ProductReviewView = ({ productId }) => {
  const dispatch = useDispatch();
  const { reviews, loading, error } = useSelector((state) => state.productReviews);

  useEffect(() => {
    dispatch(getProductReviews(productId));
  }, [dispatch, productId]);

  return (
    <Container>
      {loading ? (
        <CircularProgress />
      ) : error ? (
        <Typography variant="body1" color="error">
          {error}
        </Typography>
      ) : reviews.length === 0 ? (
        <Typography variant="body1" color="textSecondary">
          No reviews found
        </Typography>
      ) : (
        <Grid container spacing={2}>
          {reviews.map((review) => (
            <Grid item xs={12} key={review._id}>
              <Box
                sx={{
                  border: "1px solid #ddd",
                  borderRadius: "8px",
                  padding: "16px",
                  marginBottom: "16px",
                }}
              >
                <Rating name="read-only" value={review.rating} readOnly />
                <Typography variant="body1">{review.comment}</Typography>
                <Box display="flex" alignItems="center">
                  <Avatar variant="rounded">
                    {review.userId.username.charAt(0).toUpperCase()}
                  </Avatar>
                  <Typography variant="subtitle2" sx={{ marginLeft: "8px" }}>
                    By: {review.userId.username}
                  </Typography>
                  <Typography variant="caption" sx={{ marginLeft: "16px" }}>
                    {format(new Date(review.createdAt), 'dd MMM yyyy')} {/* Format the date */}
                  </Typography>
                </Box>
              </Box>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default ProductReviewView;
