import React from "react";
import {
  Box,
  Container,
  Grid,
  TextField,
  Typography,
  Button,
  RadioGroup,
  FormControlLabel,
  Radio,
  FormLabel,
  FormControl,
  Divider,
  Badge,
} from "@mui/material";
import axios from "axios";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import { useCart } from "../../../Context/CartContext";

const AddressSchema = Yup.object().shape({
  firstName: Yup.string().required("First Name is required"),
  lastName: Yup.string().required("Last Name is required"),
  phone: Yup.string().required("Phone is required"),
  email: Yup.string().email("Invalid email").required("Email is required"),
  zipcode: Yup.string().required("Zipcode is required"),
  country: Yup.string().required("Country is required"),
  state: Yup.string().required("State is required"),
  city: Yup.string().required("City is required"),
  address: Yup.string().required("Address is required"),
});

const CheckoutSchema = Yup.object().shape({
  billingAddress: AddressSchema,
  shippingAddress: AddressSchema,
  paymentMethod: Yup.string().required("Payment Method is required"),
});

const Checkout = () => {
  const { cart } = useCart();

  const calculateTotalPrice = () => {
    return (
      cart.cart?.items?.reduce(
        (total, item) => total + item.productId.price * item.quantity,
        0
      ) || 0
    );
  };

  const initialValues = {
    billingAddress: {
      firstName: "",
      lastName: "",
      phone: "",
      email: "",
      zipcode: "",
      country: "",
      state: "",
      city: "",
      address: "",
    },
    shippingAddress: {
      firstName: "",
      lastName: "",
      phone: "",
      email: "",
      zipcode: "",
      country: "",
      state: "",
      city: "",
      address: "",
    },
    paymentMethod: "COD",
    items:
      cart.cart?.items.map((item) => ({
        productId: item.productId._id,
        quantity: item.quantity,
        price: item.productId.price,
      })) || [],
    amount: calculateTotalPrice(),
  };

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      };

      const { data } = await axios.post("/api/orders", values, config);
      console.log("Order created successfully:", data);
    } catch (error) {
      console.error("Error creating order:", error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Box sx={{ mt: 2 }}>
      <Container>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6} lg={8}>
            <Typography variant="h6" sx={{ fontWeight: "bold" }}>
              Billing details
            </Typography>
            <Box sx={{ boxShadow: 3, p: 2, border: "1px solid #ccc" }}>
              <Formik
                initialValues={initialValues}
                validationSchema={CheckoutSchema}
                onSubmit={handleSubmit}
              >
                {({ isSubmitting }) => (
                  <Form>
                    <Grid container spacing={3}>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="First Name"
                          name="firstName"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Last Name"
                          name="lastName"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Phone"
                          name="phone"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Email"
                          name="email"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Address"
                          name="address"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Country"
                          name="country"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="State"
                          name="state"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="City"
                          name="city"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Field
                          as={TextField}
                          fullWidth
                          label="Zipcode"
                          name="zipcode"
                          size="small"
                          required
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <FormControl>
                          <FormLabel>Payment Method</FormLabel>
                          <RadioGroup name="paymentMethod">
                            <FormControlLabel
                              value="COD"
                              control={<Radio />}
                              label="Cash on Delivery"
                            />
                            <FormControlLabel
                              value="Razorpay"
                              control={<Radio />}
                              label="Razorpay"
                            />
                          </RadioGroup>
                        </FormControl>
                      </Grid>
                      <Grid item xs={12}>
                        <Button
                          type="submit"
                          variant="contained"
                          color="primary"
                          disabled={isSubmitting}
                        >
                          Place Order
                        </Button>
                      </Grid>
                    </Grid>
                  </Form>
                )}
              </Formik>
            </Box>
          </Grid>
          <Grid item xs={12} md={6} lg={4}>
            <Box
              sx={{
                boxShadow: 3,
                p: 2,
                border: "1px solid #ccc",
                borderRadius: 3,
              }}
            >
              <Typography variant="h6">Your Order</Typography>
              <Divider sx={{ my: 2 }} />
              {cart.cart?.items.map((item) => (
                <Box
                  key={item.productId._id}
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    mb: 1,
                  }}
                >
                  <Badge badgeContent={item.quantity} color="primary">
                    <img
                      src={item.productId.ProductImage}
                      alt={item.productId.name}
                      style={{
                        height: "60px",
                        width: "60px",
                        borderRadius: "10px",
                      }}
                    />
                  </Badge>
                  <Typography>
                    {item.productId.name} - ₹{item.productId.price}
                  </Typography>
                </Box>
              ))}
              <Divider sx={{ my: 2 }} />
              <Typography variant="h6">
                Total: ₹{calculateTotalPrice().toFixed(2)}
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Checkout;
