import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Container, Typography, Box, CircularProgress, Avatar, Rating } from '@mui/material';
import { getUserReviews } from '../../redux/actions/productActions';
import { format } from 'date-fns'; // Import date formatting library

const UserReviews = (userId) => {
  const dispatch = useDispatch();
  const { reviews, loading, error } = useSelector((state) => state.userReviews);

  useEffect(() => {
    dispatch(getUserReviews(userId));
  }, [dispatch, userId, productId]);

  return (
    <Container>
      {loading ? (
        <CircularProgress />
      ) : error ? (
        <Typography variant="body1" color="error">
          {error}
        </Typography>
      ) : reviews.length === 0 ? (
        <Typography variant="body1" color="textSecondary">
          No reviews found
        </Typography>
      ) : (
        <Box>
          {reviews.map((review) => (
            <Box key={review._id} sx={{ border: "1px solid #ddd", borderRadius: "8px", padding: "16px", marginBottom: "16px" }}>
              <Rating name="read-only" value={review.rating} readOnly />
              <Typography variant="body1">{review.comment}</Typography>
              <Box display="flex" alignItems="center" mt={2}>
                <Avatar variant="rounded">
                  {review.userId.username.charAt(0).toUpperCase()}
                </Avatar>
                <Typography variant="subtitle2" sx={{ marginLeft: "8px" }}>
                  By: {review.userId.username}
                </Typography>
                <Typography variant="caption" sx={{ marginLeft: "16px" }}>
                  {format(new Date(review.createdAt), 'dd MMM yyyy')}
                </Typography>
              </Box>
            </Box>
          ))}
        </Box>
      )}
    </Container>
  );
};

export default UserReviews;
