import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getOrderDetails } from "../../../redux/actions/productActions"; // Make sure you have this action

import {
  Container,
  Typography,
  Chip,
  CircularProgress,
  Alert,
  Grid,
  Box,
} from "@mui/material";

// Function to get chip color based on order status
const getStatusChipColor = (status) => {
  switch (status) {
    case "Pending":
      return "default";
    case "Shipped":
      return "primary";
    case "Delivered":
      return "success";
    case "Cancelled":
      return "error";
    default:
      return "default";
  }
};

// Format Date
const formatDate = (date) => {
  return new Date(date).toLocaleDateString("en-US", {
    year: "numeric",
    month: "long",
    day: "numeric",
  });
};

const OrderDetails = () => {
  const { orderId } = useParams();
  const dispatch = useDispatch();
  const orderDetails = useSelector((state) => state.orderDetails);
  const { loading, order, error } = orderDetails;

  useEffect(() => {
    dispatch(getOrderDetails(orderId));
  }, [dispatch, orderId]);

  return (
    <Container>
      {loading && <CircularProgress />}
      {error && <Alert severity="error">{error}</Alert>}
      {order ? (
        <Box>
          <Typography variant="h4">Order Details</Typography>
          <Typography variant="h6">Order ID: {order._id}</Typography>
          <Typography>Date: {formatDate(order.createdAt)}</Typography>
          <Typography>Status: {order.status}</Typography>
          <Typography>Total: ₹{order.amount}</Typography>
          <Grid container spacing={3} sx={{ marginTop: 2 }}>
            {order.items.map((item, index) => (
              <Grid item xs={12} md={6} key={index}>
                <Box
                  sx={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                  }}
                >
                  <Box>
                    <Typography>Product: {item.productDetails?.name}</Typography>
                    <Typography>Price: ₹{item.productDetails?.price}</Typography>
                    <Typography>Quantity: {item.quantity}</Typography>
                  </Box>
                  <Box>
                    <img
                      src={item.productDetails?.productImage}
                      alt={item.productDetails?.name}
                      style={{ height: "60px", width: "60px", borderRadius: "10px" }}
                    />
                  </Box>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Box>
      ) : (
        <Typography>No order found</Typography>
      )}
    </Container>
  );
};

export default OrderDetails;
