import React from "react";
import {
  Box,
  Container,
  Grid,
  TextField,
  Typography,
  Button,
  RadioGroup,
  FormControlLabel,
  Radio,
  FormLabel,
  FormControl,
  Divider,
  Badge,
} from "@mui/material";
import axios from "axios";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import { useCart } from "../../../Context/CartContext";
import { useDispatch } from "react-redux";

const AddressSchema = Yup.object().shape({
  firstName: Yup.string().required("First Name is required"),
  lastName: Yup.string().required("Last Name is required"),
  phone: Yup.string().required("Phone is required"),
  email: Yup.string().email("Invalid email").required("Email is required"),
  zipcode: Yup.string().required("Zipcode is required"),
  country: Yup.string().required("Country is required"),
  state: Yup.string().required("State is required"),
  city: Yup.string().required("City is required"),
  address: Yup.string().required("Address is required"),
});

const CheckoutSchema = Yup.object().shape({
  billingAddress: AddressSchema,
  shippingAddress: AddressSchema,
  paymentMethod: Yup.string().required("Payment Method is required"),
});

//Razopay

const Checkout = () => {
  const { cart } = useCart();
  const dispatch = useDispatch();
  const calculateTotalPrice = () => {
    return (
      cart.cart?.items?.reduce(
        (total, item) => total + item.productId.price * item.quantity,
        0
      ) || 0
    );
  };
  const handleRazorpayPayment = async (values) => {
    const res = await loadRazorpayScript();
    if (!res) {
      alert("Razorpay SDK failed to load. Please check your internet.");
      return;
    }

    const paymentData = await fetch("/api/orders/razorpay", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ amount: calculateTotalPrice() * 100 }),
    }).then((res) => res.json());

    const options = {
      key: "YOUR_RAZORPAY_KEY", // Replace with your Razorpay Key
      amount: paymentData.amount,
      currency: "INR",
      name: "Your Company",
      description: "Order Payment",
      order_id: paymentData.id,
      handler: function (response) {
        values.paymentId = response.razorpay_payment_id;
        dispatch(createOrder(values));
        alert("Payment Successful!");
      },
      prefill: {
        name:
          values.billingAddress.firstName +
          " " +
          values.billingAddress.lastName,
        email: values.billingAddress.email,
        contact: values.billingAddress.phone,
      },
      theme: {
        color: "#3399cc",
      },
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  };

  const handleSubmit = async (values, { setSubmitting }) => {
    if (values.paymentMethod === "Razorpay") {
      await handleRazorpayPayment(values);
    } else {
      dispatch(createOrder(values));
      alert("Order Placed Successfully!");
    }
    setSubmitting(false);
  };

  return (
    <Box sx={{ mt: 2 }}>
      <Container>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6} lg={8}>
            <Box sx={{ p: 2 }}>
              <Formik
                initialValues={{
                  billingAddress: {
                    firstName: "",
                    lastName: "",
                    phone: "",
                    email: "",
                    zipcode: "",
                    country: "",
                    state: "",
                    city: "",
                    address: "",
                  },
                  shippingAddress: {
                    firstName: "",
                    lastName: "",
                    phone: "",
                    email: "",
                    zipcode: "",
                    country: "",
                    state: "",
                    city: "",
                    address: "",
                  },
                  paymentMethod: "COD",
                  amount: calculateTotalPrice(),
                }}
                validationSchema={CheckoutSchema}
                onSubmit={handleSubmit}
              >
                {({ isSubmitting }) => (
                  <Form>
                    <Grid container spacing={3}>
                      {["billingAddress", "shippingAddress"].map((type) => (
                        <Grid key={type} item xs={12}>
                          <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                            {type === "billingAddress"
                              ? "Billing Details"
                              : "Shipping Details"}
                          </Typography>
                          <Box
                            sx={{
                              boxShadow: 3,
                              p: 2,
                              border: "1px solid #ccc",
                            }}
                          >
                            <Grid container spacing={2}>
                              {Object.keys(AddressSchema.fields).map(
                                (field) => (
                                  <Grid key={field} item xs={12} md={6}>
                                    <Field
                                      as={TextField}
                                      fullWidth
                                      label={
                                        field.charAt(0).toUpperCase() +
                                        field.slice(1)
                                      }
                                      name={`${type}.${field}`}
                                      size="small"
                                      required
                                    />
                                  </Grid>
                                )
                              )}
                            </Grid>
                          </Box>
                        </Grid>
                      ))}
                      <Grid item xs={12}>
                        <FormControl>
                          <FormLabel>Payment Method</FormLabel>
                          <RadioGroup name="paymentMethod">
                            <FormControlLabel
                              value="COD"
                              control={<Radio />}
                              label="Cash on Delivery"
                            />
                            <FormControlLabel
                              value="Razorpay"
                              control={<Radio />}
                              label="Razorpay"
                            />
                          </RadioGroup>
                        </FormControl>
                      </Grid>
                      <Grid item xs={12}>
                        <Button
                          type="submit"
                          variant="contained"
                          color="primary"
                          disabled={isSubmitting}
                        >
                          Place Order
                        </Button>
                      </Grid>
                    </Grid>
                  </Form>
                )}
              </Formik>
            </Box>
          </Grid>
          <Grid item xs={12} md={6} lg={4}>
            <Box
              sx={{
                boxShadow: 3,
                p: 2,
                border: "1px solid #ccc",
                borderRadius: 3,
              }}
            >
              <Typography variant="h6">Your Order</Typography>
              <Divider sx={{ my: 2 }} />
              {cart.cart?.items.map((item) => (
                <Box
                  key={item.productId._id}
                  sx={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    mb: 1,
                  }}
                >
                  <Badge badgeContent={item.quantity} color="primary">
                    <img
                      src={item.productId.ProductImage}
                      alt={item.productId.name}
                      style={{
                        height: "60px",
                        width: "60px",
                        borderRadius: "10px",
                      }}
                    />
                  </Badge>
                  <Typography>
                    {item.productId.name} - ₹{item.productId.price}
                  </Typography>
                </Box>
              ))}
              <Divider sx={{ my: 2 }} />
              <Typography variant="h6">
                Total: ₹{calculateTotalPrice().toFixed(2)}
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Checkout;
