import React, { useState } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { TextField, Button, LinearProgress, Avatar } from "@mui/material";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { Grid } from "antd";

const validationSchema = Yup.object({
  username: Yup.string().required("Username is required"),
  email: Yup.string()
    .email("Invalid email address")
    .required("Email is required"),
  password: Yup.string().min(6, "Password must be at least 6 characters"),
  bio: Yup.string(),
  profession: Yup.string(),
  address: Yup.object({
    street: Yup.string(),
    city: Yup.string(),
    state: Yup.string(),
    postalCode: Yup.string(),
    country: Yup.string(),
  }),
});

const UserUpdateForm = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  const [profileImage, setProfileImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(user.profilePicture || "");

  const formik = useFormik({
    initialValues: {
      username: user?.username || "",
      email: user?.email || "",
      password: "",
      bio: user?.bio || "",
      profession: user?.profession || "",
      address: {
        street: user?.address?.street || "",
        city: user?.address?.city || "",
        state: user?.address?.state || "",
        postalCode: user?.address?.postalCode || "",
        country: user?.address?.country || "",
      },
    },
    validationSchema,
    onSubmit: async (values, { setSubmitting }) => {
      const formData = new FormData();
      formData.append("username", values.username);
      formData.append("email", values.email);
      formData.append("password", values.password);
      formData.append("bio", values.bio);
      formData.append("profession", values.profession);
      formData.append("address", JSON.stringify(values.address));
      if (profileImage) formData.append("profilePicture", profileImage);

      try {
        const response = await axios.put(
          "http://localhost:5000/api/users/update",
          formData,
          {
            headers: {
              "Content-Type": "multipart/form-data",
              Authorization: `Bearer ${localStorage.getItem("token")}`, // Replace with your token management
            },
          }
        );
        alert("Profile updated successfully!");
      } catch (error) {
        console.error("Error updating profile:", error);
      } finally {
        setSubmitting(false);
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <Avatar src={previewImage} sx={{ width: 80, height: 80, mb: 2 }} />
      <input
        accept="image/*"
        type="file"
        id="profileImage"
        hidden
        onChange={(event) => {
          const file = event.currentTarget.files[0];
          setProfileImage(file);
          setPreviewImage(URL.createObjectURL(file));
        }}
      />
      <label htmlFor="profileImage">
        <Button
          variant="contained"
          component="span"
          startIcon={<CloudUploadIcon />}
        >
          Upload Profile Picture
        </Button>
      </label>

      <Grid item xs={12} md={6}>
        <TextField
          fullWidth
          margin="normal"
          label="Username"
          name="username"
          value={formik.values.username}
          onChange={formik.handleChange}
          error={formik.touched.username && Boolean(formik.errors.username)}
          helperText={formik.touched.username && formik.errors.username}
        />
      </Grid>
      <Grid item xs={12} md={6}>
        <TextField
          fullWidth
          margin="normal"
          label="Email"
          name="email"
          value={formik.values.email}
          onChange={formik.handleChange}
          error={formik.touched.email && Boolean(formik.errors.email)}
          helperText={formik.touched.email && formik.errors.email}
        />
      </Grid>
      <TextField
        fullWidth
        margin="normal"
        label="Password"
        type="password"
        name="password"
        onChange={formik.handleChange}
      />
      <TextField
        fullWidth
        margin="normal"
        label="Bio"
        name="bio"
        value={formik.values.bio}
        onChange={formik.handleChange}
      />
      <TextField
        fullWidth
        margin="normal"
        label="Profession"
        name="profession"
        value={formik.values.profession}
        onChange={formik.handleChange}
      />

      {/* Address Fields */}
      <TextField
        fullWidth
        margin="normal"
        label="Street"
        name="address.street"
        value={formik.values.address.street}
        onChange={formik.handleChange}
      />
      <TextField
        fullWidth
        margin="normal"
        label="City"
        name="address.city"
        value={formik.values.address.city}
        onChange={formik.handleChange}
      />
      <TextField
        fullWidth
        margin="normal"
        label="State"
        name="address.state"
        value={formik.values.address.state}
        onChange={formik.handleChange}
      />
      <TextField
        fullWidth
        margin="normal"
        label="Postal Code"
        name="address.postalCode"
        value={formik.values.address.postalCode}
        onChange={formik.handleChange}
      />
      <TextField
        fullWidth
        margin="normal"
        label="Country"
        name="address.country"
        value={formik.values.address.country}
        onChange={formik.handleChange}
      />

      {formik.isSubmitting && <LinearProgress sx={{ my: 2 }} />}
      <Button
        type="submit"
        variant="contained"
        color="primary"
        disabled={formik.isSubmitting}
      >
        Update Profile
      </Button>
    </form>
  );
};

export default UserUpdateForm;
