import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Container,
  Typography,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableRow,
  Paper,
  IconButton,
  Box,
  useMediaQuery,
  Card,
  CardMedia,
  CardContent,
} from "@mui/material";
import { Add, Remove, Delete } from "@mui/icons-material";
import {
  GetAddToCart,
  removeFromCart,
  addToCart,
} from "../redux/actions/productActions";
import EmptyCart from "../assets/image/emptycart.png";
import { SecondaryButton } from "../Global/Button/MuiButton";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const cartList = useSelector((state) => state.cartList);
  const { loading, cart, error } = cartList.cart || {};
  const isMobile = useMediaQuery("(max-width:600px)");

  const user = JSON.parse(localStorage.getItem("user"));
  const userId = user?._id;

  useEffect(() => {
    if (userId) {
      dispatch(GetAddToCart(userId));
    }
  }, [dispatch, userId]);

  const handleRemove = (productId) => {
    dispatch(removeFromCart(productId, userId)).then(() => {
      dispatch(GetAddToCart(userId));
    });
  };

  const handleIncrease = (productId, quantity) => {
    dispatch(addToCart(userId, productId, quantity + 1)).then(() => {
      dispatch(GetAddToCart(userId));
    });
  };

  const handleDecrease = (productId, quantity) => {
    if (quantity > 1) {
      dispatch(addToCart(userId, productId, quantity - 1)).then(() => {
        dispatch(GetAddToCart(userId));
      });
    } else {
      dispatch(removeFromCart(productId, userId)).then(() => {
        dispatch(GetAddToCart(userId));
      });
    }
  };

  const calculateTotalPrice = () => {
    return (
      cart?.items?.reduce(
        (total, item) => total + item.productId.price * item.quantity,
        0
      ) || 0
    );
  };

  return (
    <Container>
      <Box sx={{ m: 2 }}>
        {loading && <CircularProgress />}
        {error && <Typography color="error">{error}</Typography>}

        {cart?.items?.length > 0 ? (
          <>
            {isMobile ? (
              cart.items.map((item) => (
                <Card
                  key={item.productId._id}
                  sx={{ display: "flex", mb: 2, boxShadow: 2, borderRadius: 2,
                   }}
                >
                  <img
                    src={item.productId.ProductImage}
                    alt={item.productId.name}
                    style={{
                      height: "80px",
                      width: "80px",
                      borderRadius: "10px",
                      mt:2
                    }}
                  />
                  <CardContent sx={{ flex: 1 }}>
                    <Typography variant="h6" fontWeight="bold">
                      {item.productId.name}
                    </Typography>
                    <Typography variant="body2">
                      Price: ${item.productId.price}
                    </Typography>
                    <Typography variant="body2">
                      Total: $
                      {(item.productId.price * item.quantity).toFixed(2)}
                    </Typography>
                    <Box
                      sx={{
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "space-between",
                        mt: 1,
                      }}
                    >
                      <Box>
                        <IconButton
                          onClick={() =>
                            handleDecrease(item.productId._id, item.quantity)
                          }
                        >
                          <Remove />
                        </IconButton>
                        {item.quantity}
                        <IconButton
                          onClick={() =>
                            handleIncrease(item.productId._id, item.quantity)
                          }
                        >
                          <Add />
                        </IconButton>
                      </Box>
                      <IconButton
                        color="error"
                        onClick={() => handleRemove(item.productId._id)}
                      >
                        <Delete />
                      </IconButton>
                    </Box>
                  </CardContent>
                </Card>
              ))
            ) : (
              <TableContainer
                component={Paper}
                sx={{
                  overflowX: "auto",
                  borderRadius: 2,
                  boxShadow: 3,
                  maxWidth: "100%",
                }}
              >
                <Table>
                  <TableBody>
                    {cart.items.map((item) => (
                      <TableRow key={item.productId._id}>
                        <TableCell>
                          <img
                            src={item.productId.ProductImage}
                            alt={item.productId.name}
                            style={{
                              height: "80px",
                              width: "80px",
                              borderRadius: "10px",
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <Typography fontWeight="bold">
                            {item.productId.name}
                          </Typography>
                        </TableCell>
                        <TableCell align="right">
                          ${item.productId.price}
                        </TableCell>
                        <TableCell align="right">
                          <IconButton
                            onClick={() =>
                              handleDecrease(item.productId._id, item.quantity)
                            }
                          >
                            <Remove />
                          </IconButton>
                          {item.quantity}
                          <IconButton
                            onClick={() =>
                              handleIncrease(item.productId._id, item.quantity)
                            }
                          >
                            <Add />
                          </IconButton>
                        </TableCell>
                        <TableCell align="right">
                          ${(item.productId.price * item.quantity).toFixed(2)}
                        </TableCell>
                        <TableCell align="center">
                          <IconButton
                            color="error"
                            onClick={() => handleRemove(item.productId._id)}
                          >
                            <Delete />
                          </IconButton>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            )}

            <Box mt={4} p={2} bgcolor="#f5f5f5" borderRadius={2}>
              <Typography variant="h5" align="right">
                Total Price: ${calculateTotalPrice().toFixed(2)}
              </Typography>
            </Box>
          </>
        ) : (
          <Box
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              flexDirection: "column",
              m: 2,
            }}
          >
            <Box sx={{ textAlign: "center" }}>
              <img
                src={EmptyCart}
                alt="Empty Cart"
                style={{
                  width: "100px",
                  height: "100px",
                  textAlign: "center",
                  margin: "auto",
                }}
              />
              <Typography sx={{ fontSize: 20, fontWeight: 500, my: 1 }}>
                Your cart is empty.
              </Typography>
              <SecondaryButton
                text="START SHOPPING"
                onClick={() => navigate("/collection")}
              />
            </Box>
          </Box>
        )}
      </Box>
    </Container>
  );
};

export default Cart;
