  const getStatusChipColor = (status) => {
    switch (status) {
      case "Pending":
        return "#213555";
      case "Shipped":
        return "primary";
      case "Delivered":
        return "#6E8E59";
      case "Cancelled":
        return "#BE3144";
      default:
        return "default";
